import axios from "axios";
import React, { Component } from "react";
import { Form, Button, Row, Col, Container } from "react-bootstrap";
import { getItem } from "../../services/LocaleStorage";

export default class NewEventForm extends Component {
  constructor() {
    super();
    this.state = {
      title: "",
      place: "",
      picture: "",
      selectFile: null,
      description: "",
      picture: "",
      typeEvent: "",
      urlEvent: "",
      price: 0,
      nbrPlace: 0,
      startEvent: new Date(),
      endEvent: new Date(),
    };
  }
  onFileChange = (e) => {
    // Update the state
    this.setState({ selectFile: e.target.files[0] });
  };

  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  onSaveEvent = (e) => {
    var token = getItem("miniblogToken");
    const config = {
      headers: { Authorization: `Bearer ${token}` },
    };
    e.preventDefault();
    const data = new FormData();
    data.append("file", this.state.selectFile);
    data.append("picture", this.state.picture);
    data.append("title", this.state.title);
    data.append("place", this.state.place);
    data.append("description", this.state.description);
    data.append("typeEvent", this.state.typeEvent);
    data.append("urlEvent", this.state.urlEvent);
    data.append("price", this.state.price);
    data.append("nbrPlace", this.state.nbrPlace);
    data.append("startEvent", this.state.startEvent);
    data.append("endEvent", this.state.endEvent);

    axios
      .post(`${process.env.REACT_APP_API_URL}api/event`, data, config)
      .then((res) => {
        this.props.history.push("/list-event");
      })
      .catch((err) => {
        console.log(err);
      });
  };

  render() {
    return (
      <Container>
        <Row>
          <h3>Nouveau évennement:</h3>
          <div className="add_event">
            <Form>
              <Row className="mb-3">
                <Form.Group as={Col}>
                  <Form.Label>Titre</Form.Label>
                  <Form.Control
                    type="text"
                    id="title"
                    name="title"
                    placeholder="Enter un titre"
                    onChange={this.onChange}
                    value={this.state.title}
                  />
                </Form.Group>

                <Form.Group as={Col}>
                  <Form.Label>Lieu</Form.Label>
                  <Form.Control
                    type="text"
                    name="place"
                    id="place"
                    placeholder="Entrer un lieu"
                    onChange={this.onChange}
                    value={this.state.place}
                  />
                </Form.Group>
              </Row>

              <Row className="mb-3">
                <Form.Group as={Col}>
                  <Form.Label>Type d'évennement</Form.Label>
                  <Form.Select
                    id="typeEvent"
                    name="typeEvent"
                    value={this.state.typeEvent}
                    onChange={this.onChange}
                  >
                    <option value="camping">Camping</option>
                    <option value="Journée détente">Journée détente</option>
                    <option value="Natation">Natation</option>
                    <option value="Vélo de Montagne">Vélo de Montagne</option>
                    <option value="Running-trail">Running-trail</option>
                    <option value="Randonné">Randonné</option>
                    <option value="Randonnée pédestre">
                      Randonnée pédestre
                    </option>
                    <option value="Kayak">Kayak</option>
                    <option value="Saut pendulaire">Saut pendulaire</option>
                    <option value="Baignade">Baignade</option>
                    <option value="Paintball">Paintball</option>
                    <option value="Spéléologie">Spéléologie</option>
                    <option value="Table d'hotes">Table d'hotes</option>
                    <option value="Catamaran">Catamaran</option>
                  </Form.Select>
                </Form.Group>
                <Form.Group as={Col}>
                  <Form.Label>Prix:</Form.Label>
                  <Form.Control
                    type="number"
                    name="price"
                    id="price"
                    onChange={this.onChange}
                    value={this.state.price}
                  />
                </Form.Group>

                <Form.Group as={Col}>
                  <Form.Label>Nombre de places max:</Form.Label>
                  <Form.Control
                    type="number"
                    name="nbrPlace"
                    id="nbrPlace"
                    onChange={this.onChange}
                    value={this.state.nbrPlace}
                  />
                </Form.Group>
              </Row>

              <Row className="mb-3">
                <Form.Group as={Col}>
                  <Form.Label>Date debut:</Form.Label>
                  <Form.Control
                    type="date"
                    id="startEvent"
                    name="startEvent"
                    onChange={this.onChange}
                    value={this.state.startEvent}
                  />
                </Form.Group>
                <Form.Group as={Col}>
                  <Form.Label>Date fin:</Form.Label>
                  <Form.Control
                    type="date"
                    id="endEvent"
                    name="endEvent"
                    onChange={this.onChange}
                    value={this.state.endEvent}
                  />
                </Form.Group>
                <Form.Group as={Col}>
                  <Form.Label>Lien Reservartion:</Form.Label>
                  <Form.Control
                    type="text"
                    id="urlEvent"
                    name="urlEvent"
                    onChange={this.onChange}
                    value={this.state.urlEvent}
                  />
                </Form.Group>
                <Form.Group as={Col}>
                  <Form.Label>photo:</Form.Label>
                  <Form.Control
                    type="file"
                    id="selectFile"
                    name="selectFile"
                    onChange={this.onFileChange}
                  />
                </Form.Group>
              </Row>
              <Form.Group className="mb-3">
                <Form.Label>Description de l'évennement</Form.Label>
                <Form.Control
                  as="textarea"
                  rows={3}
                  type="text"
                  name="description"
                  id="description"
                  onChange={this.onChange}
                  value={this.state.description}
                />
              </Form.Group>

              <Button variant="primary" onClick={this.onSaveEvent}>
                Valider
              </Button>
            </Form>
          </div>
        </Row>
      </Container>
    );
  }
}
