import React, { useState, useEffect } from "react";
import { Form, Button, Row, Col, Container } from "react-bootstrap";
import axios from "axios";
import { getItem } from "../../services/LocaleStorage";

const UpdateEvent = ({ props }) => {
  const [title, setTitle] = useState("");
  const [place, setPlace] = useState("");
  const [description, setDescription] = useState("");
  const [typeEvent, setTypeEvent] = useState("");
  const [urlEvent, setUrlEvent] = useState("");
  const [price, setPrice] = useState(0);
  const [nbrPlace, setNbrPlace] = useState(0);
  const [startEvent, setStartEvent] = useState("");
  const [endEvent, setEndEvent] = useState("");
  const [file, setFile] = useState();
  const [fetchData, setFetchData] = useState(null);

  useEffect(() => {
    const fetchdata = async () => {
      var token = getItem("miniblogToken");
      const config = {
        headers: { Authorization: `Bearer ${token}` },
      };

      axios
        .get(
          `${process.env.REACT_APP_API_URL}api/event/` + props.match.params._id,
          config
        )
        .then((res) => {
          console.log("donnÃ©e ", res);
          setFetchData(res.data);
        })
        .catch((err) => console.log(err));
    };
    fetchdata();
    console.log("id", props.match.params._id);
  }, []);

  const updatePost = async (e) => {
    var token = getItem("miniblogToken");
    const config = {
      headers: { Authorization: `Bearer ${token}` },
    };
    e.preventDefault();
    const data = new FormData();
    data.append("title", fetchData.title);
    data.append("place", fetchData.place);
    data.append("description", fetchData.description);
    data.append("typeEvent", fetchData.typeEvent);
    data.append("urlEvent", fetchData.urlEvent);
    data.append("price", fetchData.price);
    data.append("nbrPlace", fetchData.nbrPlace);
    data.append("startEvent", fetchData.startEvent);
    data.append("endEvent", fetchData.endEvent);
    data.append("file", file);
    axios
      .put(
        `${process.env.REACT_APP_API_URL}api/event/` + props.match.params._id,
        data,
        config
      )
      .then((res) => {
        console.log("objet enrg", res);
        // history.push("/list-event");
      })
      .catch((err) => console.log("erreur encoi", err));
  };

  return (
    <Container>
      <Row>
        <h3>Nouveau Ã©vennement:</h3>
        <div className="add_event">
          <Form>
            <Row className="mb-3">
              <Form.Group as={Col}>
                <Form.Label>Titre</Form.Label>
                <Form.Control
                  type="text"
                  id="title"
                  name="title"
                  placeholder="Enter un titre"
                  onChange={(e) => setTitle(e.target.value)}
                  value={title}
                />
              </Form.Group>

              <Form.Group as={Col}>
                <Form.Label>Lieu</Form.Label>
                <Form.Control
                  type="text"
                  name="place"
                  id="place"
                  placeholder="Entrer un lieu"
                  onChange={(e) => setPlace(e.target.value)}
                  value={place}
                />
              </Form.Group>
            </Row>

            <Row className="mb-3">
              <Form.Group as={Col}>
                <Form.Label>Type d'Ã©vennement</Form.Label>
                <Form.Select
                  id="typeEvent"
                  name="typeEvent"
                  value={typeEvent}
                  onChange={(e) => setTypeEvent(e.target.value)}
                  placeholder="choisir"
                >
                  <option> choisir</option>
                  <option value="JournÃ©e dÃ©tente">JournÃ©e dÃ©tente</option>
                  <option value="Natation">Natation</option>
                  <option value="VÃ©lo de Montagne">VÃ©lo de Montagne</option>
                  <option value="Running-trail">Running-trail</option>
                  <option value="RandonnÃ©">RandonnÃ©</option>
                  <option value="RandonnÃ©e pÃ©destre">RandonnÃ©e pÃ©destre</option>
                  <option value="Kayak">Kayak</option>
                  <option value="Saut pendulaire">Saut pendulaire</option>
                  <option value="Baignade">Baignade</option>
                  <option value="Paintball">Paintball</option>
                  <option value="SpÃ©lÃ©ologie">SpÃ©lÃ©ologie</option>
                  <option value="Table d'hotes">Table d'hotes</option>
                  <option value="Catamaran">Catamaran</option>
                </Form.Select>
              </Form.Group>
              <Form.Group as={Col}>
                <Form.Label>Prix:</Form.Label>
                <Form.Control
                  type="number"
                  name="price"
                  id="price"
                  onChange={(e) => setPrice(e.target.value)}
                  value={price}
                />
              </Form.Group>

              <Form.Group as={Col}>
                <Form.Label>Nombre de places max:</Form.Label>
                <Form.Control
                  type="number"
                  name="nbrPlace"
                  id="nbrPlace"
                  onChange={(e) => setNbrPlace(e.target.value)}
                  value={nbrPlace}
                />
              </Form.Group>
            </Row>

            <Row className="mb-3">
              <Form.Group as={Col}>
                <Form.Label>Date debut:</Form.Label>
                <Form.Control
                  type="date"
                  id="startEvent"
                  name="startEvent"
                  onChange={(e) => setStartEvent(e.target.value)}
                  value={startEvent}
                />
              </Form.Group>
              <Form.Group as={Col}>
                <Form.Label>Date fin:</Form.Label>
                <Form.Control
                  type="date"
                  id="endEvent"
                  name="endEvent"
                  onChange={(e) => setEndEvent(e.target.value)}
                  value={endEvent}
                />
              </Form.Group>
              <Form.Group as={Col}>
                <Form.Label>Lien Reservartion:</Form.Label>
                <Form.Control
                  type="text"
                  id="urlEvent"
                  name="urlEvent"
                  onChange={(e) => setUrlEvent(e.target.value)}
                  value={urlEvent}
                />
              </Form.Group>
              <Form.Group as={Col}>
                <Form.Label>photo:</Form.Label>
                <Form.Control
                  type="file"
                  id="file"
                  name="file"
                  accept=".jpg, .jpeg, .png"
                  onChange={(e) => setFile(e.target.files[0])}
                />
              </Form.Group>
            </Row>
            <Form.Group className="mb-3">
              <Form.Label>Description de l'Ã©vennement</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                type="text"
                name="description"
                id="description"
                onChange={(e) => setDescription(e.target.value)}
                value={description}
              />
            </Form.Group>

            <Button variant="primary" onClick={updatePost}>
              Valider
            </Button>
          </Form>
        </div>
      </Row>
    </Container>
  );
};

export default UpdateEvent;
